create schema if not exists "browser";

create table "browser"."histories" (
    "user_id" uuid not null,
    "id" bigint generated by default as identity not null,
    "media_id" integer not null,
    "type" text not null,
    "season" integer not null default 0,
    "episode" integer not null default 0,
    "duration" double precision not null default 0,
    "last_position" double precision not null default 0,
    "completed" boolean not null default false,
    "adult" boolean not null,
    "backdrop_path" text,
    "poster_path" text,
    "release_date" date not null,
    "title" text not null,
    "vote_average" numeric(4,1) not null,
    "created_at" timestamp with time zone not null default now(),
    "updated_at" timestamp with time zone not null default now()
);


alter table "browser"."histories" enable row level security;

create table "browser"."profiles" (
    "id" uuid not null,
    "username" text not null,
    "created_at" timestamp with time zone default now()
);


alter table "browser"."profiles" enable row level security;

create table "browser"."watchlist" (
    "user_id" uuid not null,
    "id" integer not null,
    "type" text not null,
    "adult" boolean not null,
    "backdrop_path" text,
    "poster_path" text,
    "release_date" date not null,
    "title" text not null,
    "vote_average" numeric(4,1) not null,
    "created_at" timestamp with time zone not null default now()
);


alter table "browser"."watchlist" enable row level security;

CREATE UNIQUE INDEX histories_pkey ON browser.histories USING btree (id);

CREATE UNIQUE INDEX histories_user_id_media_id_type_season_episode_key ON browser.histories USING btree (user_id, media_id, type, season, episode);

CREATE INDEX histories_user_updated_idx ON browser.histories USING btree (user_id, updated_at DESC);

CREATE UNIQUE INDEX profiles_pkey ON browser.profiles USING btree (id);

CREATE UNIQUE INDEX profiles_username_key ON browser.profiles USING btree (username);

CREATE UNIQUE INDEX watchlist_pkey ON browser.watchlist USING btree (user_id, id, type);

alter table "browser"."histories" add constraint "histories_pkey" PRIMARY KEY using index "histories_pkey";

alter table "browser"."profiles" add constraint "profiles_pkey" PRIMARY KEY using index "profiles_pkey";

alter table "browser"."watchlist" add constraint "watchlist_pkey" PRIMARY KEY using index "watchlist_pkey";

alter table "browser"."histories" add constraint "histories_type_check" CHECK ((type = ANY (ARRAY['movie'::text, 'tv'::text]))) not valid;

alter table "browser"."histories" validate constraint "histories_type_check";

alter table "browser"."histories" add constraint "histories_user_id_fkey" FOREIGN KEY (user_id) REFERENCES auth.users(id) ON DELETE CASCADE not valid;

alter table "browser"."histories" validate constraint "histories_user_id_fkey";

alter table "browser"."histories" add constraint "histories_user_id_media_id_type_season_episode_key" UNIQUE using index "histories_user_id_media_id_type_season_episode_key";

alter table "browser"."profiles" add constraint "profiles_id_fkey" FOREIGN KEY (id) REFERENCES auth.users(id) ON DELETE CASCADE not valid;

alter table "browser"."profiles" validate constraint "profiles_id_fkey";

alter table "browser"."profiles" add constraint "profiles_username_key" UNIQUE using index "profiles_username_key";

alter table "browser"."watchlist" add constraint "watchlist_type_check" CHECK ((type = ANY (ARRAY['movie'::text, 'tv'::text]))) not valid;

alter table "browser"."watchlist" validate constraint "watchlist_type_check";

alter table "browser"."watchlist" add constraint "watchlist_user_id_fkey" FOREIGN KEY (user_id) REFERENCES auth.users(id) ON DELETE CASCADE not valid;

alter table "browser"."watchlist" validate constraint "watchlist_user_id_fkey";

set check_function_bodies = off;

CREATE OR REPLACE FUNCTION browser.set_updated_at()
 RETURNS trigger
 LANGUAGE plpgsql
 SET search_path TO ''
AS $function$
begin
  new.updated_at = now();
  return new;
end;
$function$
;

